import { Fragment, useContext, useState } from 'react';
import { CartItemsContext } from './Cart';
import Box from '@mui/material/Box';
import Modal from '@mui/material/Modal';
import ShoppingCartIcon from '@mui/icons-material/ShoppingCart';
import Badge from '@mui/material/Badge';
import CartCard from './CartCard/CartCard';
import './Cart.css';
import Button from '@mui/material/Button';

const style = {
  position: 'absolute',
  top: '50%',
  left: '50%',
  transform: 'translate(-50%, -50%)',
  minWidth: '350px',
  width: '45%',
  height: '400px',
  bgcolor: 'background.paper',
  border: '5px solid #FFE26E',
  borderRadius: '15px',
  boxShadow: 24,
  p: 4,
};

const Cart = () => {
  const [open, setOpen] = useState(false);
  const handleOpen = () => setOpen(true);
  const handleClose = () => setOpen(false);

  const [openCheckoutModal, setOpenCheckoutModal] = useState(false);
  const handleCheckoutOpen = () => setOpenCheckoutModal(true);
  const handleCheckoutClose = () => setOpenCheckoutModal(false);

  const { items, totalAmount, resetCart } = useContext(CartItemsContext);

  // Simulate the checkout process
  const handleCheckout = async () => {
    if (totalAmount > 0) {
      // Simulate a successful checkout (No actual API call)
      console.log("Payment successful!");

      // Show "Payment Done" message
      alert("Payment Done");

      // Reset the cart
      resetCart(); // This clears the cart and totalAmount in the context

      // Optionally, close the cart modal and show checkout success modal
      handleCheckoutOpen();
    } else {
      alert("Your cart is empty. Please add items to the cart.");
    }
  };

  return (
    <Fragment>
      <Badge badgeContent={items.length} color="error">
        <ShoppingCartIcon
          color="black"
          onClick={handleOpen}
          sx={{ width: '35px' }}
        />
      </Badge>
      <Modal open={open} onClose={handleClose}>
        <Box sx={style}>
          <div className="cart__header">
            <h2>Your Cart</h2>
          </div>
          <div className="cart__items__container">
            <div className="cartItems">
              {items.length === 0 ? (
                <div className="cart__empty">Empty cart!</div>
              ) : (
                <div className="shop__cart__items">
                  {items.map((item) => (
                    <CartCard key={item._id} item={item} />
                  ))}
                </div>
              )}
              {items.length > 0 && (
                <div className="options">
                  <div className="total__amount">
                    <div className="total__amount__label">Total Amount:</div>
                    <div className="total__amount__value">Rs.{totalAmount}.00</div>
                  </div>
                  <div className="checkout">
                    <Button variant="outlined" onClick={handleCheckout}>
                      Checkout
                    </Button>
                  </div>
                </div>
              )}
            </div>
          </div>
        </Box>
      </Modal>
      <Modal open={openCheckoutModal} onClose={handleCheckoutClose}>
        <Box sx={style}>
          <div className="d-flex w-100 h-100 justify-content-center align-items-center">
            <h2>Your checkout was successful</h2>
          </div>
        </Box>
      </Modal>
    </Fragment>
  );
};

export default Cart;
