import { createContext, useState, useContext } from 'react';

export const CartItemsContext = createContext();

export const CartItemsProvider = ({ children }) => {
  const [items, setItems] = useState([]);
  const [totalAmount, setTotalAmount] = useState(0);

  const addItem = (item) => {
    setItems([...items, item]);
    setTotalAmount(prev => prev + item.price);
  };

  const removeItem = (itemId) => {
    const updatedItems = items.filter(item => item._id !== itemId);
    setItems(updatedItems);

    const updatedTotalAmount = updatedItems.reduce((total, item) => total + item.price, 0);
    setTotalAmount(updatedTotalAmount);
  };

  const resetCart = () => {
    setItems([]);
    setTotalAmount(0);
  };

  return (
    <CartItemsContext.Provider value={{ items, totalAmount, addItem, removeItem, resetCart }}>
      {children}
    </CartItemsContext.Provider>
  );
};

export const useCartItems = () => useContext(CartItemsContext);
